import HttpClient from '../http/HttpClient';
export default class PublishService {
  getForumCategorys() {
    try {
      return Promise.resolve(HttpClient.get('/web/userarticle/getForumCategorys.json').then(resp => Promise.resolve(resp.data)).then(resp => {
        const result = [];

        if (resp) {
          resp.map(item => {
            result.push({
              value: item.id,
              label: item.showName,
              originalName: item.originalName
            });
          });
        }

        return Promise.resolve(result);
      }));
    } catch (e) {
      return Promise.reject(e);
    }
  }

  submit(model) {
    try {
      const newModel = {
        content: model.content,
        systemCategoryId: model.systemCategoryId,
        urls: model.urls,
        type: model.type
      };
      return Promise.resolve(HttpClient.post('/web/userarticle/insertArticles.json', newModel));
    } catch (e) {
      return Promise.reject(e);
    }
  }

}
