import { useEffect, useState } from 'react';

const useUpdateEffect = (effect, deps) => {
  const {
    0: inited,
    1: setInited
  } = useState(false);
  useEffect(() => {
    if (inited) {
      effect();
    } else {
      setInited(true);
    }
  }, deps);
};

export default useUpdateEffect;
